mapper.select:
        SELECT


--         级联表的字段

        FROM minsu  a



                and a.id in

                #{item}



                and a.minsu_uuid_number like CONCAT('%',#{params.minsuUuidNumber},'%')


                and a.minsu_name like CONCAT('%',#{params.minsuName},'%')


                and a.minsu_types = #{params.minsuTypes}


                  and a.minsu_new_money >= #{params.minsuNewMoneyStart}   


                  and a.minsu_new_money <= #{params.minsuNewMoneyEnd}   


                  and a.minsu_clicknum >= #{params.minsuClicknumStart}   


                  and a.minsu_clicknum <= #{params.minsuClicknumEnd}   


                and a.minsu_clicknum = #{params.minsuClicknum}


                and a.minsu_content like CONCAT('%',#{params.minsuContent},'%')


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.minsu_delete >= #{params.minsuDeleteStart}   


                  and a.minsu_delete <= #{params.minsuDeleteEnd}   


                and a.minsu_delete = #{params.minsuDelete}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        order by a.${params.sort} ${params.order}
